void edk::shape::Polygon3DList::deleteVBO(){
    if(this->vbo){
        edk::GU_GLSL::guDeleteBuffer(this->vbo);
        this->vbo=0u;
    }
    if(this->vertexBuffer.size()){
        this->vertexBuffer.deleteArray();
    }
    this->vboType = edk::GU::vbo_NULL;
    this->vboCount=0u;
}
